export class BandwidthService {
    /**
     * @typedef {{
     *  chunkCountForSpeed?: number,
     *  skipChunkSize?: number,
     *  longtimeNoReceived?: number
     * }} Opts
     *
     * @param {Opts} opts
     */
    constructor(opts: {
        chunkCountForSpeed?: number;
        skipChunkSize?: number;
        longtimeNoReceived?: number;
    });
    _chunkSpeed: number;
    _chunkCache: any[];
    _speeds: any[];
    _totalSize: number;
    _totalCost: number;
    _opts: {
        chunkCountForSpeed?: number;
        skipChunkSize?: number;
        longtimeNoReceived?: number;
    };
    addRecord(totalByte: any, ms: any): void;
    addChunkRecord(totalByte: any, ms: any): void;
    /**
     *
     * @returns { number }
     */
    getAvgSpeed(): number;
    /**
     *
     * @returns { number }
     */
    getLatestSpeed(): number;
    /**
     *
     * @returns { number }
     */
    getTotalSize(): number;
    /**
     *
     * @returns { number }
     */
    getTotalCost(): number;
    reset(): void;
}
